import React from "react";
import { Link } from "react-router-dom";

const Card = ({dentist}) => {
  const { id, name, username } = dentist;

  const addFav = () => {
    // Obtener el array de favoritos del localStorage o inicializar uno vacío si no existe
    const favorites = JSON.parse(localStorage.getItem('favorites')) || [];

    // Verificar si el dentista ya está en favoritos por su ID
    const isDentistInFavorites = favorites.some(fav => fav.id === id);

    if (!isDentistInFavorites) {
      // Agregar el dentista a la lista de favoritos
      favorites.push({ id, name, username });
      // Guardar la lista actualizada en el localStorage
      localStorage.setItem('favorites', JSON.stringify(favorites));
    }else {
      alert("Este dentista ya ha sido agregado a favoritos");
    }
  };

  return (
    <div className="card">
      {/* En cada card deberan mostrar en name - username y el id */}
      <h2>{name}</h2>
      <p>Id: {id}</p>
      <p>Username: {username}</p>

      {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}
      <Link to={`/Dentist/${id}`}>Details</Link>

      {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button onClick={addFav} className="favButton">Add fav</button>
    </div>
  );
};
export default Card;
